import numpy as np
import dsl
from arc_types import Grid
from dsl import objects


def same_color(x: Grid, y: Grid) -> bool:
    x_ar = np.asarray(x)
    y_ar = np.asarray(y)
    unique_x = np.unique(x_ar)
    unique_y = np.unique(y_ar)

    return np.array_equal(unique_x, unique_y)

def same_shape(x: Grid, y: Grid) -> bool:
    return len(x) == len(y) and len(x[0]) == len(y[0])

def same_objects(x: Grid, y: Grid) -> bool:
    x_objects = dsl.objects(x, univalued=False, diagonal=True, without_bg=True)
    y_objects = dsl.objects(y, univalued=False, diagonal=True, without_bg=True)

    return x_objects == y_objects

def pixel_match_percent(x: Grid, y: Grid) -> float:
    x_ar = np.asarray(x)
    y_ar = np.asarray(y)

    min_row = min(x_ar.shape[0], y_ar.shape[0])
    min_col = min(x_ar.shape[1], y_ar.shape[1])

    matches = np.sum(x_ar[:min_row, :min_col] == y_ar[:min_row, :min_col])
    total = min_row * min_col
    percent_match = matches / total
    return percent_match

def distance(x, y) -> float:
    color = 1 if same_color(x, y) else 0
    shape = 1 if same_shape(x, y) else 0
    objects = 1 if same_objects(x, y) else 0
    pixel_match = pixel_match_percent(x, y)

    distance_invert = 0.5 * pixel_match + 0.125 * color + 0.125 * shape + 0.25 * objects
    return 1 - distance_invert

